import { useState, React } from "react";
import data from "./babyNamesData.json";
import FavouriteList from "./FavouriteList";
import SplitGender from "./SplitGender";

export default function Search() {
  const [searchInput, setSearchInput] = useState("");
  function handleSearchInput(event) {
    setSearchInput(event.target.value);
  }

  const [favoriteList, setFavoriteList] = useState([]);
  function handleFavList(event) {
    setFavoriteList(favoriteList.concat(handleSearchInput(event)));
  }

  const addToFavorites = (nameId) => {
    const nameIndex = data.findIndex((name) => name.id === nameId);
    const newMainList = [...mainList];
    const removedName = newMainList.splice(nameIndex, 1);
    setMainList(newMainList);
    setFavoritesList([...favoritesList, removedName[0]]);
  };

  const filteredNames = data
    .sort((a, b) => {
      let fa = a.name;
      let fb = b.name;

      if (fa < fb) {
        return -1;
      }
      if (fa > fb) {
        return 1;
      }
      return 0;
    })
    .filter((babyName) =>
      babyName.name.toLowerCase().includes(searchInput.toLowerCase())
    )
    .map((baby) =>
      baby.sex === "m" ? (
        <span className="babyBoy" onClick={handleFavList}>
          {baby.name}
        </span>
      ) : (
        <span className="babyGirl" onClick={handleFavList}>
          {baby.name}
        </span>
      )
    );

  return (
    <>
      <div className="searchDiv">
        <input
          id="searchBar"
          type="text"
          placeholder="Search.."
          value={searchInput}
          onChange={handleSearchInput}
        />
      </div>
      <FavouriteList name={favoriteList} />
      <div className="searchOutput">
        {searchInput === "" ? <SplitGender data={data} /> : filteredNames}
      </div>
    </>
  );
}
