  const [searchInput, setSearchInput] = useState("");
  function handleSearchInput(event) {
    setSearchInput(event.target.value);
  }
  function handleSearch(event) {
    event.preventDefault();
    search(searchInput);
  }

  const filteredNames=sortedNames
        .filter((babyName) =>
          babyName.name.toUpperCase().includes(searchInput.toUpperCase())
        )
        .map((baby) =>
          baby.sex === "m" ? (
            <span className="babyBoy">{baby.name}</span>
          ) : (
            <span className="babyGirl">{baby.name}</span>
          )
        )



  return (
    <div className="searchDiv">
      <input
        id="searchBar"
        type="text"
        placeholder="Search.."
        value={searchInput}
        onChange={handleSearchInput}
      />
      {searchInput === "" ? <SplitGender /> : filteredNames}
    </div>
  );
}
